import React from "react";
import ReactApexChart from "react-apexcharts";
import CovidData from './Data';

const filteredData = data.filter((item) => {
    const date = new Date(item.isoDateString);
    const day = date.getDate();
    const month = date.getMonth();
    return [1, 3, 7, 10, 14, 17, 21, 24, 27].includes(day) && item.allCases && item.allDeaths && type === 'Cases' ? item.allCases : item.allDeaths;
  }).reduce((acc, item) => {
    const date = new Date(item.dateRep);
    const month = date.getMonth();
    const year = date.getFullYear();
    const key = `${year}-${month}`;
    if (!acc[key]) {
      acc[key] = [];
    }
    acc[key].push(item);
    return acc;
  }, {});



function Graph() {
   
  
    const series = [
      {
        name: 'Cases',
        data: covidData.map((item) => item.Allcases),
      },
      {
        name: 'Deaths',
        data: covidData.map((item) => item.Alldeaths),
      },
    ];
  
    const xaxisCategories = covidData.map((item) => item.isoDateString);
  
    const options = {
      chart: {
        height: 350,
        type: 'area',
      },
      dataLabels: {
        enabled: false,
      },
      stroke: {
        curve: 'smooth',
      },
      xaxis: {
        type: 'datetime',
        categories: xaxisCategories,
      },
      tooltip: {
        x: {
          format: 'dd/MM/yyyy',
        },
      },
    };
  
    return (
      <div id="chart">
        <ReactApexChart options={options} series={series} type="area" height={350} />
      </div>
    );
  }

  export default Graph;
