import React, { useState, useEffect } from 'react';
import { DateRangePicker } from 'react-date-range';
import 'react-date-range/dist/styles.css'; // main css file
import 'react-date-range/dist/theme/default.css'; // theme css file
import "./dateRangeComp.css";
//import 'bootstrap/dist/css/bootstrap.min.css';
import moment from 'moment';

const DateRangeComp = ({ onDateRangeChange, minDate, maxDate }) => {
  const [startDate, setStartDate] = useState(moment(minDate, 'YYYY-MM-DD'));
  const [endDate, setEndDate] = useState(moment(maxDate, 'YYYY-MM-DD'));

  useEffect(() => {
    setStartDate(moment(minDate));
    setEndDate(moment(maxDate));
  }, [minDate, maxDate]);

  const handleDatesChange = (ranges) => {
    const { startDate, endDate } = ranges.selection;
    setStartDate(startDate);
    setEndDate(endDate);

    if (onDateRangeChange) {
      onDateChange(moment(startDate), moment(endDate));
    }
  };

  const selectionRange = {
    startDate: startDate.toDate(),
    endDate: endDate.toDate(),
    key: 'selection',
  };

  return (
    <DateRangePicker
      ranges={[selectionRange]}
      onChange={handleDatesChange}
      showSelectionPreview={true}
      showMonthAndYearPickers={true}
      months={1}
      direction="horizontal"
      minDate={moment(minDate).toDate()}
      maxDate={moment(maxDate).toDate()}
    />
  );
};

export default DateRangeComp;
