import React, {useState, useEffect, useMemo, useCallback} from 'react';
import DateRangePicker from './components/DateRangeComp';
import VisualizationType from "./components/VisualizationType";
import CountrySelector from  './components/CountrySelector';
import Viewer from './components/Viewer';
import moment from 'moment';
import { CovidData } from './components/Data';
import "bootstrap/dist/css/bootstrap.min.css";
import './App.css';

const App = () => {

  const [data, setData] = useState([]);
  const [filteredData, setFilteredData] = useState([]);
  const [selectedCountry, setSelectedCountry] = useState('allCountries');
  const [dateRange, setDateRange] = useState({ startDate: moment('2019-12-31'), endDate: moment('2020-12-31') });
  const [visualizationType, setVisualizationType] = useState("table");

  const filterData = useCallback((country, dateRange) => {
    let filtered = data;

    if (country) {
      if (country === "allCountries") {
        filtered = filtered.filter(
          (item) => item.countriesAndTerritories === country
        );
      } else {
        filtered = filtered.filter(
          (item) => item.countriesAndTerritories === country
        );
      }
    }

    if (dateRange.startDate && dateRange.endDate) {
      filtered = filtered.filter((item) =>
        moment(item.date).isBetween(
          dateRange.startDate,
          dateRange.endDate,
          undefined,
          '[]'
        )
      );
    }

    setFilteredData(filtered);
  }, []);


  useEffect(() => {
    const fetchData = async () => {
      const result = await CovidData();
      setData(result);

    };
    fetchData();
  }, []);

  useEffect(() => {
    const fetchData = async () => {
      const result = await CovidData();
      setData(result);
    };
    fetchData();
  }, []);

  useEffect(() => {
    filterData(selectedCountry, dateRange);
  }, [data, dateRange, filterData, selectedCountry]);






  const handleCountryChange = (event) => {
    const country = event.target.value;
    setSelectedCountry(country);
    filterData(country, dateRange);
  };

  const handleDateRangeChange = (startDate, endDate) => {
    setDateRange({ startDate, endDate });
    filterData(selectedCountry, { startDate, endDate });
  };

  const countries = useMemo(() => {
    const uniqueCountries = Array.from(
      new Set(data.map((item) => item.countriesAndTerritories))
    );
    const sortedCountries = uniqueCountries.sort();
    const index = sortedCountries.indexOf("allCountries");
    if (index > -1) {
      sortedCountries.splice(index, 1);
    }
    return ["allCountries", ...sortedCountries];
  }, [data]);




  return (
    <div className="App">
      <h1>Covid Data Visualizer</h1>
      <DateRangePicker dateRange={dateRange} handleDateRangeChange={handleDateRangeChange} />
      <VisualizationType
        visualizationType={visualizationType}
        setVisualizationType={setVisualizationType}
      />
      <CountrySelector
        countries={countries}
        selectedCountry={selectedCountry}
        handleCountryChange={handleCountryChange}
      />
      <Viewer
        dateRange={dateRange}
        visualizationType={visualizationType}
        selectedCountry={selectedCountry}
        filteredData={filteredData}
      />

    </div>
  );
};

export default App;
