import React, { useState, useEffect } from 'react';
import {CovidData} from './components/Data';
import DateRangePickerComponent from './components/DateRangePicker';
import CountrySelector from './components/CountrySelector';
import ViewSwitcher from './components/ViewSwitcher';
import './App.css';

const App = () => {
  const [data, setData] = useState([]);
  const [selectedCountry, setSelectedCountry] = useState(null);
  const [filteredData, setFilteredData] = useState([]);
  const [dateRange, setDateRange] = useState({ startDate: '', endDate: '' });
  const [currentView, setCurrentView] = useState('table');

  useEffect(() => {

    const filterData = () => {
      let filtered = data;

      if (selectedCountry) {
        filtered = filtered.filter((item) => item.country === selectedCountry.value);
      }

      if (dateRange.startDate && dateRange.endDate) {
        filtered = filtered.filter((item) => {
          const itemDate = new Date(item.date);
          const startDate = new Date(dateRange.startDate);
          const endDate = new Date(dateRange.endDate);
          return itemDate >= startDate && itemDate <= endDate;
        });
      }

      setFilteredData(filtered);
    };

    filterData();
  }, [data, selectedCountry, dateRange]);

  const handleDataFetched = (fetchedData) => {
    setData(fetchedData);
  };

  const handleDateRangeChange = ({ startDate, endDate }) => {
    setDateRange({ startDate, endDate });
  };

  const handleCountryChange = (country) => {
    setSelectedCountry(country);
  };

  const handleViewChange = (view) => {
    setCurrentView(view);
  };

  return (
    <div className="App">
      <h1>Data Visualizer</h1>
      <DataFetcher onDataFetched={handleDataFetched} />
      <DateRange onDateRangeChange={handleDateRangeChange} />
      <CountrySelector onCountryChange={handleCountryChange} />
      <ViewSwitcher
        currentView={currentView}
        onViewChange={handleViewChange}
        filteredData={filteredData}
      />
    </div>
  );
};

export default App;
