const Pagination = ({
  data = [],
  pageIndex,
  pageCount,
  canPreviousPage,
  previousPage,
  canNextPage,
  nextPage,
  gotoPage,
  pageOptions,
}) => {
  return (
    <div className="pagination">
      ...
      <span>
        | Go to page:{' '}
        <input
          type="number"
          defaultValue={pageIndex + 1}
          onChange={(e) => {
            const pageNumber = e.target.value ? Number(e.target.value) - 1 : 0;
            gotoPage(pageNumber);
          }}
          style={{ width: '50px' }}
        />
      </span>{' '}
      ...
    </div>
  );
};
