import React, { useState, useEffect, useMemo } from 'react';
import { useTable, usePagination } from 'react-table';
import { COLUMNS } from './columns';
import { CovidData } from './Data';
import DateRangeComp from './DateRangeComp';
import CountrySelector from './CountrySelector';
import Pagination from './Pagination';
import moment from 'moment';
import './table.css';

const Table = () => {
  const columns = useMemo(() => COLUMNS, []);
  const [data, setdData] = useState([]);
  const [filteredData, setFilteredData] = useState([]);
  const [selectedCountry, setSelectedCountry] = useState('allCountries');
  const [dateRange, setDateRange] = useState({ startDate: null, endDate: null });

  useEffect(() => {
    const fetchData = async () => {
      const data = await CovidData();
      setTableData(data);
      setFilteredData(data);
    };

    fetchData();
  }, []);

  useEffect(() => {
    filterData(selectedCountry, dateRange);
  }, [tableData]);


  const filterData = (country, dateRange) => {
    let filtered = tableData;

    if (country) {
      if (country === "allCountries") {
        filtered = filtered.filter(
          (item) => item.countriesAndTerritories === country
        );
      } else {
        filtered = filtered.filter(
          (item) => item.countriesAndTerritories === country
        );
      }
    }
  
    if (dateRange.startDate && dateRange.endDate) {
      filtered = filtered.filter((item) =>
        moment(item.date).isBetween(
          dateRange.startDate,
          dateRange.endDate,
          undefined,
          '[]'
        )
      );
    }
  
    setFilteredData(filtered);
  };

  const handleCountryChange = (event) => {
    const country = event.target.value;
    setSelectedCountry(country);
    filterData(country, dateRange);
  };

  const handleDateRangeChange = (startDate, endDate) => {
    setDateRange({ startDate, endDate });
    filterData(selectedCountry, { startDate, endDate });
  };

  const countries = useMemo(() => {
    const uniqueCountries = Array.from(
      new Set(tableData.map((item) => item.countriesAndTerritories))
    );
    const sortedCountries = uniqueCountries.sort();
    const index = sortedCountries.indexOf("allCountries");
    if (index > -1) {
      sortedCountries.splice(index, 1);
    }
    return ["allCountries", ...sortedCountries];
  }, [tableData]);


  const {
    getTableProps,
    getTableBodyProps,
    headerGroups,
    page,
    nextPage,
    previousPage,
    canNextPage,
    canPreviousPage,
    pageOptions,
    state,
    prepareRow,
  } = useTable(
    {
      columns,
      data: filteredData,
      initialState: { pageIndex: 0, pageSize: 20 },
    },
    usePagination
  );

  const {pageIndex} = state




  return (
    <>
      <DateRangeComp
        onDateRangeChange={handleDateRangeChange}
        minDate={moment('2019-12-31')}
        maxDate={moment('2020-12-31')}
      />
      <CountrySelector
        countries={countries}
        selectedCountry={selectedCountry}
        handleCountryChange={handleCountryChange}
      />
      <table {...getTableProps()}>
        <thead>
          {headerGroups.map((headerGroup) => (
            <tr {...headerGroup.getHeaderGroupProps()}>
              {headerGroup.headers.map((column) => (
                <th {...column.getHeaderProps()}>{column.render('Header')}</th>
              ))}
            </tr>
          ))}
        </thead>
        <tbody {...getTableBodyProps()}>
          {page.map((row) => {
            prepareRow(row);
            return (
              <tr {...row.getRowProps()}>
                {row.cells.map((cell) => (
                  <td {...cell.getCellProps()}>{cell.render('Cell')}</td>
                ))}
              </tr>
            );
          })}
        </tbody>
      </table>
      <Pagination
        pageIndex={pageIndex}
        pageOptions={pageOptions}
        canPreviousPage={canPreviousPage}
        previousPage={previousPage}
        canNextPage={canNextPage}
        nextPage={nextPage}
      />
    </>
  );
};

export default Table;
